<?php    //INSERT FUNCTION    function insert_categories(){        global $connection;        if (isset($_POST['submit'])){            $cat_title = $_POST['cat_title'];            if ($cat_title === "" || empty($_POST['cat_title'])){                echo "<span style='color: red'>This Field Should Not be Empty</span>";            }else{                $query = "INSERT INTO categories(cat_title)VALUES ('$cat_title')";                $create_category_query = mysqli_query($connection,$query);                if (!$create_category_query){                    die('QUERY FAILED'.mysqli_error($connection));                }            }        }    }    //UPDATE FUNCTION    function update_category(){        global $connection;        if (isset($_POST['editSubmit'])){            $cat_title = $_POST['cat_title'];            $cat_id = $_POST['cat_id'];            if ($cat_title === "" || empty($_POST['cat_title'])){                echo "<span style='color: red'>This Field Should Not be Empty</span>";            }else{                $query = "UPDATE categories SET cat_title ='$cat_title' WHERE cat_id = '$cat_id'";                $update_category_query = mysqli_query($connection,$query);                if (!$update_category_query){                    die('QUERY FAILED'.mysqli_error($connection));                }            }        }    }    //DELETE FUNCTION    function delete_category(){        global $connection;        if (isset($_GET['delete'])){            $delete_cat_id = $_GET['delete'];            $query = mysqli_query($connection, "DELETE FROM categories WHERE cat_id = $delete_cat_id");            header("Location: categories.php");        }    }