<form action="#" method="post" enctype="multipart/form-data">    <div class="form-group col-md-12">        <label for="post_title">Post Title <span class="text-danger">*</span></label>        <input class="form-control" name="post_title" type="text">    </div>    <div class="form-group col-md-6">        <label for="post_category_id">Post Category Id <span class="text-danger">*</span></label>        <input class="form-control" name="post_category_id" type="text">    </div>    <div class="form-group col-md-6">        <label for="post_author">Post Author <span class="text-danger">*</span></label>        <input class="form-control" name="post_author" type="text">    </div>    <div class="form-group col-md-12">        <label for="post_status">Post Title <span class="text-danger">*</span></label>        <input class="form-control" name="post_status" type="text">    </div>    <div class="form-group col-md-12">        <label for="">Post Image <span class="text-danger">*</span></label>        <input type="file" class="form-control" id="customFile">        <label name="image" class="custom-file-label" for="customFile">Choose file</label>    </div>    <div class="form-group col-md-12">        <label for="post_status">Post Tags <span class="text-danger">*</span></label>        <input class="form-control" name="post_tags" type="text">    </div>    <div class="form-group col-md-12">        <label for="post_status">Post Content <span class="text-danger">*</span></label>        <textarea class="form-control" placeholder="Leave a comment here" name="post_content" style="height: 150px"></textarea>    </div>    <div class="form-group col-md-12">       <button class="btn btn-primary" type="submit" name="create_post">Publish Post</button>    </div></form>