<table class="table table-bordered table-hover">    <thead>    <tr class="bg-primary">        <th scope="col">Id</th>        <th scope="col">Comment Post Id</th>        <th scope="col">Author</th>        <th scope="col">Email</th>        <th scope="col">Comment</th>        <th scope="col">Status</th>        <th scope="col">Date</th>        <th scope="col">In Response</th>        <th scope="col" class="text-center">Action</th>    </tr>    </thead>    <tbody>    <?php    $query = "SELECT * FROM comments";    $select_comments = mysqli_query($connection, $query);    foreach ($select_comments as $select_comment) {        ?>        <tr>            <th scope="col"><?php echo $select_comment['comment_id']; ?></th>            <td><?php echo $select_comment['comment_post_id']; ?></td>            <td><?php echo $select_comment['comment_author']; ?></td>            <td><?php echo $select_comment['comment_email']; ?></td>            <td><?php echo $select_comment['comment_content']; ?></td>            <td><?php echo $select_comment['comment_status']; ?></td>            <td><?php echo $select_comment['comment_date']; ?></td>            <td><?php echo $select_comment['comment_date']; ?></td>            <!--ACTION BUTTON-->            <td class=" text-center" width="15%">                <a class="btn btn-success btn-sm" href="">Approve</a>                <a class="btn btn-success btn-sm" href="">Un Approve</a>                <a class="btn btn-primary btn-sm" href="posts.php?source=edit_post&p_id=<?php echo $select_comment['post_id']; ?>">Edit</a>                <!-- POST DELETE -->                <?php                if (isset($_GET['delete'])) {                    $delete_post_id = $_GET['delete'];                    $query = mysqli_query($connection, "DELETE FROM posts WHERE post_id = $delete_post_id");                    header("Location: posts.php");                }                ?>                <a class="btn btn-danger btn-sm" href="posts.php?delete=<?php echo $select_comment['post_id']; ?>">Delete</a>            </td>        </tr>    <?php } ?>    </tbody></table>