<table class="table table-bordered table-hover">    <thead>    <tr class="bg-primary">        <th scope="col">Id</th>        <th scope="col">Image</th>        <th scope="col">User Name</th>        <th scope="col">First Name</th>        <th scope="col">Last Name</th>        <th scope="col">Email</th>        <th scope="col">Role</th>        <th scope="col" class="text-center">Action</th>    </tr>    </thead>    <tbody>    <?php    $query = "SELECT * FROM users";    $select_users = mysqli_query($connection, $query);    foreach ($select_users as $select_user) { ?>        <tr>            <th class="text-center" scope="col"><?php echo $select_user['user_id']; ?></th>            <td class="text-center"><img width="50px" src="../users_image/<?php echo $select_user['user_image']; ?>" alt=""></td>            <td><?php echo $select_user['username']; ?></td>            <td><?php echo $select_user['user_firstname']; ?></td>            <td><?php echo $select_user['user_lastname']; ?></td>            <td><?php echo $select_user['user_email']; ?></td>            <td><?php echo $select_user['user_role']; ?></td>            <!--ACTION FUNCTION-->            <td class=" text-center" width="15%">                <a class="btn btn-primary btn-sm" href="users.php?edit=<?php echo $select_user['user_id']; ?>">Edit</a>                <!-- POST DELETE -->                <?php                if (isset($_GET['delete'])) {                    $delete_user = $_GET['delete'];                    $query = mysqli_query($connection, "DELETE FROM users WHERE user_id = $delete_user");                    header("Location: users.php");                }                ?>                <a class="btn btn-danger btn-sm" href="users.php?delete=<?php echo $select_user['user_id']; ?>">Delete</a>            </td>        </tr>    <?php } ?>    </tbody></table>